// Convert Markdown notes to HTML
const notes = $('Webhook').first().json.notes;
const roomName = $('Webhook').first().json.roomName;
const timestamp = $('Webhook').first().json.timestamp;
const recipient = $input.first().json;

// Simple markdown to HTML conversion
let html = notes
  .replace(/^### (.+)$/gm, '<h3>$1</h3>')
  .replace(/^## (.+)$/gm, '<h2>$1</h2>')
  .replace(/^# (.+)$/gm, '<h1>$1</h1>')
  .replace(/\*\*(.+?)\*\*/g, '<strong>$1</strong>')
  .replace(/\*(.+?)\*/g, '<em>$1</em>')
  .replace(/^- (.+)$/gm, '<li>$1</li>')
  .replace(/(<li>.*<\/li>\n?)+/g, '<ul>$&</ul>')
  .replace(/\n\n/g, '</p><p>')
  .replace(/^(.+)$/gm, '<p>$1</p>')
  .replace(/<p><\/p>/g, '')
  .replace(/<p>(<h[123]>)/g, '$1')
  .replace(/(<\/h[123]>)<\/p>/g, '$1')
  .replace(/<p>(<ul>)/g, '$1')
  .replace(/(<\/ul>)<\/p>/g, '$1');

const meetingDate = new Date(timestamp).toLocaleDateString('en-US', {
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric',
  hour: '2-digit',
  minute: '2-digit'
});

return {
  json: {
    email: recipient.email,
    participantName: recipient.participantName,
    roomName: roomName,
    meetingDate: meetingDate,
    notesHtml: html,
    notesMarkdown: notes
  }
};

